代码123456789101112131415161718192021222324252627282930313233343536373839404142434445464748495051525354555657585960616263646566676869707172737475767778798081828384858687import osimport reimport wxfrom pathlib import Path&quot;&quot;&quot;使用python编写一个exe，实现批量修改图片引用，将修改后的文件生成为 文件名_blog.md。有一个编辑框，允许接收拖动过来md文件，拖入文件时获取文件路径，有一个编辑框编辑修改后的文件的输出路径，用户拖入文件时，就能自动得到输出的路径作用是将md文件中的例如![image-20240706062921362](./[git]git拯救项目之恢复到之前提交的记录/image-20240706062921362.png)改成&#123;% asset_img image-20240706062921362.png &#x27;&quot;...&quot; &quot;文章配图&quot;&#x27; %&#125; ![image-20240706063059015](./[git]git拯救项目之恢复到之前提交的记录/image-20240706063059015.png)改成&#123;% asset_img image-20240706063059015.png &#x27;&quot;...&quot; &quot;文章配图&quot;&#x27; %&#125;&quot;&quot;&quot;class DropTarget(wx.FileDropTarget):    def __init__(self, window):        super().__init__()        self.window = window    def OnDropFiles(self, x, y, filenames):        self.window.set_filenames(filenames)class MainFrame(wx.Frame):    def __init__(self, parent, title):        super().__init__(parent, title=title, size=(600, 400))        panel = wx.Panel(self)        self.text_input = wx.TextCtrl(panel, style=wx.TE_MULTILINE | wx.TE_READONLY)        self.text_output = wx.TextCtrl(panel, style=wx.TE_MULTILINE | wx.TE_READONLY)        self.btn_convert = wx.Button(panel, label=&quot;开始转换&quot;)        vbox = wx.BoxSizer(wx.VERTICAL)        vbox.Add(self.text_input, proportion=1, flag=wx.EXPAND | wx.ALL, border=5)        vbox.Add(self.text_output, proportion=1, flag=wx.EXPAND | wx.ALL, border=5)        vbox.Add(self.btn_convert, flag=wx.EXPAND | wx.ALL, border=5)        panel.SetSizer(vbox)        self.SetDropTarget(DropTarget(self))        self.Bind(wx.EVT_BUTTON, self.on_convert, self.btn_convert)        self.Centre()        self.Show(True)    def set_filenames(self, filenames):        self.filenames = filenames        self.text_input.SetValue(&#x27;\n&#x27;.join(filenames))    def on_convert(self, event):        for filename in self.filenames:            if filename.lower().endswith(&#x27;.md&#x27;):                input_file_path = Path(filename)                output_file_path = input_file_path.with_name(input_file_path.stem + &#x27;_blog&#x27; + input_file_path.suffix)                self.text_output.AppendText(str(output_file_path) + &#x27;\n&#x27;)                self.convert_markdown_images(str(input_file_path), str(output_file_path))    def convert_markdown_images(self, input_file, output_file):        with open(input_file, &#x27;r&#x27;, encoding=&#x27;utf-8&#x27;) as file:            content = file.read()        # 修改正则表达式，以匹配Markdown图片链接        pattern = r&#x27;\!\[(?P&lt;alt_text&gt;.*?)\]\((?P&lt;path&gt;.*/)?(?P&lt;file_name&gt;.*?)(?P&lt;extension&gt;\..*)\)&#x27;        # 遍历所有匹配项并构建新的替换字符串        new_content = re.sub(pattern, lambda m: f&#x27;&#123;&#123;% asset_img &#123;m.group(&quot;file_name&quot;)&#125;&#123;m.group(&quot;extension&quot;)&#125; \&#x27;&quot;&#123;m.group(&quot;alt_text&quot;)&#125;&quot; &quot;文章配图&quot;\&#x27; %&#125;&#125;&#x27;, content)        # 将修改后的内容写入输出文件        with open(output_file, &#x27;w&#x27;, encoding=&#x27;utf-8&#x27;) as file:            file.write(new_content)        wx.MessageBox(f&quot;已成功转换&#x27;&#123;input_file&#125;&#x27;至&#x27;&#123;output_file&#125;&#x27;&quot;, &quot;转换成功&quot;, wx.OK)if __name__ == &#x27;__main__&#x27;:    app = wx.App()    MainFrame(None, title=&quot;Markdown 图片链接转换器&quot;)    app.MainLoop()if __name__ == &#x27;__main__&#x27;:    app = wx.App()    MainFrame(None, title=&quot;Markdown Image Link Converter@萌狼蓝天(mllt.cc)&quot;)    app.MainLoop()

